import React from 'react';
import { Component } from 'react';
import API from '../utils/API';
import './TableCSS.css';
import Rows from './Rows';

export default class Table extends Component {
  state = {
    employee: [],
    location: '',
  };

  componentDidMount() {
    API.getEmployees().then(({ data }) => {
      this.setState({ employee: data.results });
    });
  }

  clearFilters = (e) => {
    e.preventDefault();
    API.getEmployees().then(({ data }) => {
      this.setState({ employee: data.results });
    });
  };

  filterAlphabetically = () => {
    const filtered = this.state.employee.sort((a, b) =>
      a.name.last > b.name.last ? 1 : b.name.last > a.name.last ? -1 : 0
    );
    this.setState({ employee: filtered });
  };

  filterByState = (location) => (employee) => {
    const loc = employee.location.state;
    return loc.toLowerCase() === location.toLowerCase();
  };

  onChangeProp = (e) => {
    const value = e.target.value;
    const name = e.target.name;
    this.setState({
      [name]: value,
    });
  };

  renderUserFilter = (event) => {
    event.preventDefault();
    const input = event.target.children[0].children[1].value;
    const matches = this.state.employee.filter(this.filterByState(input));
    this.setState({ employee: matches, location: '' });
    console.log(event);
  };

  render() {
    return (
      <>
        <div className="container">
          <form
            id="filterForm"
            className="form-inline"
            onSubmit={this.renderUserFilter}
          >
            <div className="form-group mb-2">
              <button
                className="btn btn-primary"
                onClick={this.filterAlphabetically}
              >
                Sort Alphabetically
              </button>
              <button
                id="clearFilters"
                type="submit"
                className="btn btn-primary"
                onClick={this.clearFilters}
              >
                Clear Filters
              </button>
            </div>
            <div id="locationFilter" className="form-group mb-2">
              <label className="mr-3">Filter by Location:</label>
              <input
                type="text"
                className="form-contol mr-1"
                placeholder="Enter State Here"
                value={this.state.location}
                onChange={this.onChangeProp}
                name="location"
                aria-label="location"
                aria-describedby="searchState"
              ></input>
              <button
                type="submit"
                className="btn btn-primary"
                onClick={this.filterAlphabetically}
                id="searchState"
              >
                Search
              </button>
            </div>
          </form>
          <div className="row">
            <table className="table">
              <thead>
                <tr>
                  <th>
                    <p> </p>
                  </th>
                  <th>
                    <h4>First</h4>
                  </th>
                  <th>
                    <h4>Last</h4>
                  </th>
                  <th>
                    <h4>Email</h4>
                  </th>
                  <th>
                    <h4>Phone</h4>
                  </th>
                  <th>
                    <h4>State</h4>
                  </th>
                </tr>
              </thead>
              <tbody>
                {this.state.employee.map((employee, i) => (
                  <Rows
                    key={i}
                    firstName={employee.name.first}
                    lastName={employee.name.last}
                    email={employee.email}
                    phone={employee.phone}
                    location={employee.location.state}
                    image={employee.picture.medium}
                  />
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </>
    );
  }
}
